import React, {useState} from 'react'

const Todo = () => {

    const [item, setItem, meter] = useState('')

    return(
        <>
        <h3>해야 할 일 목록</h3>
        <h5>{item}</h5>
        <input onChange={meter => meter.target.value}/>
        <div>
        <button onClick={() => setItem(meter)}>할 일 추가</button>
        <button onClick={() => setItem('')}>할 일 삭제</button>
        </div>
        </>
    )
}

export default Todo
