import React, {useState} from 'react'

const Counter = () => {

    const [number, setNumber] = useState(0)

    const onPlusClick = () => {
        setNumber(number + 1)
    }

    const onMinusClick = () => {
        setNumber(number - 1)
    }
    const onDoubleBtnClick = () =>
    {
        setNumber(number * 2)
    }
    const onHalfBtnClick = () =>
    {
        setNumber(number / 2)
    }

    return(
        <>
        <h1>{number}</h1>
        <button onClick={onPlusClick}> + </button>
        <button onClick={onMinusClick}> - </button>
        <button onClick={onDoubleBtnClick}> X2 </button>
        <button onClick={onHalfBtnClick}> %2 </button>
        </>
    )
}

export default Counter
