import React, {useState} from 'react'

const Counter = () => {

    const [number, setNumber] = useState(0)

    const onPlusClick = () => {
        setNumber(number + 1)
    }

    const onMinusClick = () => {
        setNumber(number - 1)
    }

    return(
        <>
        <button onClick={onPlusClick}> + </button>
        <button onClick={onMinusClick}> - </button>
        </>
    )
}

export default Counter
